export const submitValidation = (forms, setForms) => {
	Object.keys(forms).map((form) => {
		setForms(prev => ({
			...prev,
			[form]: {
				...prev[form],
				validation: {
					isError: true,
					message: "Harus diisi"
				}
			}
		}))
	});
};
