import React from 'react';
import 'App.css';
import {Routes, Route, Outlet, Link, useLocation} from "react-router-dom";
const Sidebar = React.lazy(() => import('component/Sidebar'));
const Login = React.lazy(() => import('container/Login'));
const Protected = React.lazy(() => import('container/Protected'));
const RequireAuth = React.lazy(() => import('component/RequireAuth'));


function App() {
  return (
    <div className="App">
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route index element={<Protected />} />
          <Route
            path="protected"
            element={
              <React.Suspense fallback={<>...</>}>
                <RequireAuth>
                  <Protected />
                </RequireAuth>
              </React.Suspense>
            }
          />
          <Route
            path="login"
            element={
              <React.Suspense fallback={<>...</>}>
                <Login />
              </React.Suspense>
            }
          />
        </Route>
      </Routes>
    </div>
  );
}

function Layout() {
  const history = useLocation();
  const isLoginPage = history.pathname === "/login";
  console.log("history", history)
  return (
    <div>
      { isLoginPage ? null : <Sidebar/>}
      <main style={{ marginLeft: `${isLoginPage ? 0 : 25}%` }}>
        <Outlet />
      </main>
    </div>
  );
}

export default App;
